pragma solidity ^0.6.6;

import "./child/ChildToken/ChildERC20.sol";
import "./ISapienController.sol";
import "./ISapienPlatformSPN.sol";


contract SapienPlatformSPN is ISapienPlatformSPN, ChildERC20 {

	address public controller;
	address public owner;

	constructor (string memory _name, string memory _symbol, uint8 _decimals) public ChildERC20(_name, _symbol, _decimals) {
		owner = _msgSender();

	}

	modifier onlyOwner() {
		require(owner == _msgSender(), "Only owner can call this");
		_;
	}

	function changeOwner(address _owner) public onlyOwner
	{
		owner = _owner;
	}


	/// change controller address
	function changeController(address _controller) public onlyOwner
	{
		controller = _controller;
	}

	/// @dev Function that is called when a user or another contract wants to transfer funds.
	/// @param to Address of token receiver.
	/// @param value Number of tokens to transfer.
	/// @return Returns success of function call.
	function transfer(address to, uint256 value) public override(ERC20, ISapienPlatformSPN) returns (bool) {
		return transferWithPurpose(to, value, hex"");
	}

	/// @dev Function that is called when a user or another contract wants to transfer funds, including a purpose.
	/// @param to Address of token receiver.
	/// @param value Number of tokens to transfer.
	/// @param purpose Arbitrary data attached to the transaction.
	/// @return Returns success of function call.
	function transferWithPurpose(address to, uint256 value, bytes memory purpose) public returns (bool) {
		if (!ISapienController(controller).beforeTransfer(_msgSender(), to, value, purpose)) {
			return false;
		}
		return super.transfer(to, value);
	}

	function transferFrom(address sender, address recipient, uint256 amount) public override(ERC20, ISapienPlatformSPN) returns (bool) {
		return super.transferFrom(sender, recipient, amount);
	}

	// balanceOf() is not virtual in OpenZeppelin, so it cannot be overriden, need to use an alias
	function stakeBalanceOf(address account) public override view returns (uint256) {
		return balanceOf(account);
	}

}

