pragma solidity ^0.6.6;

import "./child/ChildToken/ChildERC20.sol";
import "./ISapienController.sol";
import "./ISapienPlatformSPN.sol";


contract SapienPlatformSPN is ISapienPlatformSPN, ChildERC20 {

	address public controller;
	address public owner;

	constructor (string memory _name, string memory _symbol, uint8 _decimals, address _childChainManager) public ChildERC20(_name, _symbol, _decimals, _childChainManager) {
		owner = _msgSender();

	}

	modifier onlyOwner() {
		require(owner == _msgSender(), "Only owner can call this");
		_;
	}

	function changeOwner(address _owner) public onlyOwner
	{
		owner = _owner;
	}


	/// change controller address
	function changeController(address _controller) public onlyOwner
	{
		controller = _controller;
	}

	/// @dev Function that is called when a user or another contract wants to transfer funds.
	/// @param _to Address of token receiver.
	/// @param _value Number of tokens to transfer.
	/// @return Returns success of function call.
	function transfer(address _to, uint256 _value) public override(ERC20, ISapienPlatformSPN) returns (bool) {
		require(transferWithPurpose(_to, _value, hex""));
		return true;
	}

	/// @dev Function that is called when a user or another contract wants to transfer funds, including a purpose.
	/// @param _to Address of token receiver.
	/// @param _value Number of tokens to transfer.
	/// @param _purpose Arbitrary data attached to the transaction.
	/// @return Returns success of function call.
	function transferWithPurpose(address _to, uint256 _value, bytes memory _purpose) public returns (bool) {
		require(ISapienController(controller).isTransferAllowed(_msgSender(), _to));
		require(ISapienController(controller).beforeTransfer(_msgSender(), _to, _value, _purpose));
		require(super.transfer(_to, _value));
		return true;
	}

	/// @dev Function that is called when a user or another contract wants to transfer funds.
	/// @param _sender Address of token sender.
	/// @param _recipient Address of token recipient.
	/// @param _amount Number of tokens to transfer.
	/// @return Returns success of function call.
	function transferFrom(address _sender, address _recipient, uint256 _amount) public override(ERC20, ISapienPlatformSPN) returns (bool) {
		require(ISapienController(controller).isTransferAllowed(_sender, _recipient));
		require(super.transferFrom(_sender, _recipient, _amount));
		return true;
	}

	/// @dev Function that is called when a user or another contract wants to transfer funds to multiple recipients
	/// @param _beneficiaries Array of addresses of token recipients.
	/// @param _extraTokens Array of token amounts to send.
	function transferBatch(address[] memory _beneficiaries, uint256[] memory _extraTokens) public {
	    // Check that the arrays are the same size
	    uint256 count = _beneficiaries.length;
            require(_extraTokens.length == count, "Parameter counts do not match");

            // BENEFICIARIES get tokens.
            for (uint256 i = 0; i < count; i++) {
                uint256 tokens = _extraTokens[i];
                require(transfer(_beneficiaries[i], tokens));
            }
	}

	// balanceOf() is not virtual in OpenZeppelin, so it cannot be overriden, need to use an alias.
	// this function is called by staking contract, because it cannot call balanceOf due to inheritance issues, but can call this alias function.
	function stakeBalanceOf(address _account) public override view returns (uint256) {
		return balanceOf(_account);
	}

	/// @dev Function that is called when a user or another contract wants to withdraw funds to mainnet
	/// @param _amount Number of tokens to withdraw.
	function withdraw(uint256 _amount) public override(ChildERC20, ISapienPlatformSPN) {
		require(ISapienController(controller).isWithdrawalAllowed(_msgSender()));
		require(ISapienController(controller).beforeWithdrawal(_msgSender(), _amount));
		super.withdraw(_amount);
	}
}

