pragma solidity "0.6.6";

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/math/SafeMath.sol";
import { NetworkAgnostic } from "./common/NetworkAgnostic.sol";
import "./ISapienPlatformSPN.sol";
import "./ISapienStaking.sol";   
import "./ChainConstants.sol";


contract SapienStaking is ISapienStaking, Ownable, NetworkAgnostic, ChainConstants {

    using SafeMath for uint;    

    uint constant SAFE_UINT = uint(2)**uint(60); // Numbers that can be multiplied by seconds.

    struct Stake {
        uint tokens;                // Total amount of stake
        uint timestamp;             // Time of last update
    }

    mapping(address => Stake) stakes;
    address public controller;
    address public assistant;
    address public slasher;
    address public tokenContract;

    event Staked(address indexed holder, uint extraTokens);
    event Unstaked(address indexed holder, uint releasedTokens);
    event Slashed(address indexed holder, uint tokens, bytes reason);

    constructor() public NetworkAgnostic("SapienStaking", ERC712_VERSION, ROOT_CHAIN_ID) {}

    // changing admin addresses ------------------------------------------------------------------------------

    function changeController(address _controller) public onlyOwner
    {
        controller = _controller;
    }

    function changeAssistant(address _assistant) public onlyOwner
    {
        assistant = _assistant;
    }

    function changeSlasher(address _slasher) public onlyOwner
    {
        slasher = _slasher;
    }

    function changeTokenContract(address _tokenContract) public onlyOwner
    {
        tokenContract = _tokenContract;
    }


    // data getters --------------------------------------------------------------------------------------------
    function getStake(address _holder) public view
        returns (uint tokens, uint timestamp)
    {
        Stake storage stake = stakes[_holder];
        return (stake.tokens, stake.timestamp);
    }


    // staking ------------------------------------------------------------------------------

    // stake tokens for beneficiary
    function _stakeFor(address _beneficiary, uint _extraTokens) internal {

        require(_beneficiary != address(0));
        require(_beneficiary != address(this));
        require(_extraTokens < SAFE_UINT);

        // BENEFICIARY gets the stakes.
        Stake storage stake = stakes[_beneficiary];
        uint currentTokens = stake.tokens;

        stake.tokens = currentTokens.add(_extraTokens);
        stake.timestamp = now;

        // Report the added stakes
        emit Staked(_beneficiary, _extraTokens);
    }

    // stake tokens, called by the controller contract only
    function autoStake(address _beneficiary, uint256 _value) public override returns(bool) {
        // only controller can call this, no meta tx allowed
        require(msg.sender == controller);

        _stakeFor(_beneficiary, _value);
    }

    // stake tokens batch for beneficiaries
    function stakeBatch(address[] memory _beneficiaries, uint[] memory _extraTokens) public {
        // Check that the arrays are the same size
        uint count = _beneficiaries.length;
        require(_extraTokens.length == count);

        uint totalTokens = 0;

        // BENEFICIARIES get the stakes.
        for (uint i = 0; i < count; i++) {
            uint tokens = _extraTokens[i];
            _stakeFor(_beneficiaries[i], tokens);
            totalTokens = totalTokens.add(tokens);
        }

        // SENDER pays tokens.
	// can be called by anyone directly, no meta tx allowed
        require(ISapienPlatformSPN(tokenContract).transferFrom(msg.sender, address(this), totalTokens));
    }


    // unstaking ------------------------------------------------------------------------------

    // unstaked staked tokens for sender
    function _unstakeFor(address _sender, uint _requestedTokens) internal {

        // SENDER unlocks his stakes.
        Stake storage stake = stakes[_sender];
        uint currentTokens = stake.tokens;

	uint unstakeTokens = _requestedTokens;

        // Cannot withdraw more than current tokens.
        if(unstakeTokens > currentTokens) {
            unstakeTokens = currentTokens;
        }

        require(unstakeTokens > 0);

        // Unstake tokens.
        stake.tokens = currentTokens.sub(unstakeTokens);
	stake.timestamp = now;

        // SENDER gets the tokens.
        require(ISapienPlatformSPN(tokenContract).transfer(_sender, unstakeTokens));

        emit Unstaked(_sender, unstakeTokens);
    }

    // unstake staked tokens
    function unstake(uint _requestedTokens) public {
	// can be called by anyone, meta tx support
        _unstakeFor(_msgSender(), _requestedTokens);
    }

    // unstake staked tokens on behalf of the sender
    function unstakeAssisted(address _sender, uint _requestedTokens) public {
	// only assistant can call this, no meta tx allowed
        require(msg.sender == assistant);
        _unstakeFor(_sender, _requestedTokens);
    }


    // slashing ------------------------------------------------------------------------------

    // slash staked tokens for holder and send them back to contract owner
    function slash(address _holder, uint _tokens, bytes memory _reason) public {
	// only slasher can call this, no meta tx allowed
        require(msg.sender == slasher);
        address to = owner();

        uint slashTokens = _tokens;
        uint currentTokens = stakes[_holder].tokens;

        // Cannot slash more than the balance
        if(slashTokens > currentTokens) {
            slashTokens = currentTokens;
        }

        stakes[_holder].tokens = currentTokens.sub(slashTokens);
	stakes[_holder].timestamp = now;
        require(ISapienPlatformSPN(tokenContract).transfer(to, slashTokens));

        emit Slashed(_holder, slashTokens, _reason);
    }

    function _msgSender()
        internal
        override
        view
        returns (address payable sender)
    {
        if (msg.sender == address(this)) {
            bytes memory array = msg.data;
            uint256 index = msg.data.length;
            assembly {
                // Load the 32 bytes word from memory with the address on the lower 20 bytes, and mask those.
                sender := and(
                    mload(add(array, index)),
                    0xffffffffffffffffffffffffffffffffffffffff
                )
            }
        } else {
            sender = msg.sender;
        }
        return sender;
    }


}
