pragma solidity ^0.6.6;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/math/SafeMath.sol";
import {NativeMetaTransaction} from "./common/NativeMetaTransaction.sol";
import "./ISapienPlatformSPN.sol";
import "./ISapienStaking.sol";
import "./ChainConstants.sol";


contract SapienStaking is ISapienStaking, Ownable, NativeMetaTransaction, ChainConstants {

	using SafeMath for uint256;

	struct Stake {
		uint256 tokens;                // Total amount of stake
		uint256 timestamp;             // Time of last update
	}

	mapping(address => Stake) stakes;
	address public controller;
	address public assistant;
	address public slasher;
	address public tokenContract;

	event Staked(address indexed holder, uint256 extraTokens);
	event Unstaked(address indexed holder, uint256 releasedTokens);
	event Slashed(address indexed holder, uint256 tokens, bytes reason);

	// changing admin addresses ------------------------------------------------------------------------------

	function changeController(address _controller) public onlyOwner
	{
		controller = _controller;
	}

	function changeAssistant(address _assistant) public onlyOwner
	{
		assistant = _assistant;
	}

	function changeSlasher(address _slasher) public onlyOwner
	{
		slasher = _slasher;
	}

	function changeTokenContract(address _tokenContract) public onlyOwner
	{
		tokenContract = _tokenContract;
	}


	// data getters --------------------------------------------------------------------------------------------
	function getStake(address _holder) public view returns (uint256 tokens, uint256 timestamp)
	{
		Stake storage stake = stakes[_holder];
		return (stake.tokens, stake.timestamp);
	}

	function totalStaked() public view returns (uint256) {
		return ISapienPlatformSPN(tokenContract).stakeBalanceOf(address(this));
	}

	// staking ------------------------------------------------------------------------------

	// stake tokens for beneficiary
	function _stakeFor(address _beneficiary, uint256 _extraTokens) internal {

		require(_beneficiary != address(0), "Beneficiary address is zero");
		require(_beneficiary != address(this), "Beneficiary address is staking contract");

		// BENEFICIARY gets the stakes.
		Stake storage stake = stakes[_beneficiary];
		uint256 currentTokens = stake.tokens;

		stake.tokens = currentTokens.add(_extraTokens);
		stake.timestamp = now;

		// Report the added stakes
		emit Staked(_beneficiary, _extraTokens);
	}

	// stake tokens, called by the controller contract only
	function autoStake(address _beneficiary, uint256 _value) public override {
		// only controller can call this, no meta tx allowed
		require(msg.sender == controller, "Only controller can call this");

		_stakeFor(_beneficiary, _value);
	}

	// stake tokens batch for beneficiaries
	function stakeBatch(address[] memory _beneficiaries, uint256[] memory _extraTokens) public {
		// Check that the arrays are the same size
		uint256 count = _beneficiaries.length;
		require(_extraTokens.length == count, "Parameter counts do not match");

		uint256 totalTokens = 0;

		// BENEFICIARIES get the stakes.
		for (uint256 i = 0; i < count; i++) {
			uint256 tokens = _extraTokens[i];
			_stakeFor(_beneficiaries[i], tokens);
			totalTokens = totalTokens.add(tokens);
		}

		// SENDER pays tokens.
		// can be called by anyone directly, no meta tx allowed
		require(ISapienPlatformSPN(tokenContract).transferFrom(msg.sender, address(this), totalTokens), "transferFrom() failed");
	}


	// unstaking ------------------------------------------------------------------------------

	// unstaked staked tokens for sender
	function _unstakeFor(address _sender, uint256 _requestedTokens) internal {

		// SENDER unlocks his stakes.
		Stake storage stake = stakes[_sender];
		uint256 currentTokens = stake.tokens;

		uint256 unstakeTokens = _requestedTokens;

		// Cannot withdraw more than current tokens.
		if (unstakeTokens > currentTokens) {
			unstakeTokens = currentTokens;
		}

		require(unstakeTokens > 0, "Cannot unstake zero tokens");

		// Unstake tokens.
		stake.tokens = currentTokens.sub(unstakeTokens);
		stake.timestamp = now;

		// SENDER gets the tokens.
		require(ISapienPlatformSPN(tokenContract).transfer(_sender, unstakeTokens), "transfer() call failed");

		emit Unstaked(_sender, unstakeTokens);
	}

	// unstake staked tokens
	function unstake(uint256 _requestedTokens) public {
		// can be called by anyone, meta tx support
		_unstakeFor(_msgSender(), _requestedTokens);
	}

	// unstake staked tokens on behalf of the sender
	function unstakeAssisted(address _sender, uint256 _requestedTokens) public {
		// only assistant can call this, no meta tx allowed
		require(msg.sender == assistant, "Only assistant can call this");
		_unstakeFor(_sender, _requestedTokens);
	}


	// slashing ------------------------------------------------------------------------------

	// slash staked tokens for holder and send them back to contract owner
	function slash(address _holder, uint256 _tokens, bytes memory _reason) public {
		// only slasher can call this, no meta tx allowed
		require(msg.sender == slasher, "Only slasher can call this");
		address to = owner();

		uint256 slashTokens = _tokens;
		uint256 currentTokens = stakes[_holder].tokens;

		// Cannot slash more than the balance
		if (slashTokens > currentTokens) {
			slashTokens = currentTokens;
		}

		stakes[_holder].tokens = currentTokens.sub(slashTokens);
		stakes[_holder].timestamp = now;
		require(ISapienPlatformSPN(tokenContract).transfer(to, slashTokens), "transfer() call failed");

		emit Slashed(_holder, slashTokens, _reason);
	}

}
